import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Proposal.css';
import axios from 'axios';

const Proposal = () => {
  const [groupId, setGroupId] = useState('');
  const [students, setStudents] = useState([
    { name: '', id: '', marks: '' },
    { name: '', id: '', marks: '' },
    { name: '', id: '', marks: '' },
    { name: '', id: '', marks: '' },
  ]);
  const [isSuccess, setIsSuccess] = useState(false);

  useEffect(() => {
    // Check if there is a success flag in local storage
    const successFlag = localStorage.getItem('submissionSuccess');
    if (successFlag) {
      // Display success message
      setIsSuccess(true);
      // Clear the success flag from local storage
      localStorage.removeItem('submissionSuccess');
    }
  }, []);

  const handleGroupIdChange = (event) => {
    setGroupId(event.target.value);
  };

  const handleStudentChange = (event, index, field) => {
    const updatedStudents = [...students];
    updatedStudents[index][field] = event.target.value;
    setStudents(updatedStudents);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Check if group ID is filled
    if (!groupId) {
      alert('Group ID is required.');
      return;
    }

    // Check if any student name is filled but not the ID or marks
    const incompleteStudents = students.filter(student => student.name && (!student.id || !student.marks));
    if (incompleteStudents.length > 0) {
      alert('Please fill in ID and marks for all students whose names are provided.');
      return;
    }

    try {
      await axios.post('http://localhost:8000/enter-marks/proposal', { groupId, students });
      setIsSuccess(true); // Set success flag
      // Store success flag in local storage
      localStorage.setItem('submissionSuccess', 'true');
    } catch (err) {
      console.error(err);
      // Handle error or show an error message
    }
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="proposal-container">
        <h2>Enter Proposal Marks</h2>
        {isSuccess && <div className="success-msg">Data submitted successfully!</div>}
        <form onSubmit={handleSubmit} className="proposal-form">
          <div className="group-id-section">
            <label htmlFor="groupId">Group Registration ID:</label>
            <input
              type="text"
              id="groupId"
              value={groupId}
              onChange={handleGroupIdChange}
              placeholder="Enter group ID"
            />
          </div>
          <div className="student-sections">
            {students.map((student, index) => (
              <div key={index} className="student-section">
                <h4>Student {index + 1}</h4>
                <div className="student-details">
                  <div>
                    <label htmlFor={`student${index}Name`}>Name:</label>
                    <input
                      type="text"
                      id={`student${index}Name`}
                      value={student.name}
                      onChange={(event) => handleStudentChange(event, index, 'name')}
                      placeholder="Enter student name"
                    />
                  </div>
                  <div>
                    <label htmlFor={`student${index}Id`}>ID:</label>
                    <input
                      type="text"
                      id={`student${index}Id`}
                      value={student.id}
                      onChange={(event) => handleStudentChange(event, index, 'id')}
                      placeholder="Enter student ID"
                    />
                  </div>
                  <div>
                    <label htmlFor={`student${index}Marks`}>Marks:</label>
                    <input
                      type="number"
                      id={`student${index}Marks`}
                      value={student.marks}
                      onChange={(event) => handleStudentChange(event, index, 'marks')}
                      placeholder="Enter marks"
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default Proposal;
