import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Document.css';

const Document = () => {
  const [files, setFiles] = useState({
    topicAssessmentForm: [],
    projectCharter: [],
    statusDocument1: [],
    logBook: [],
    proposalDocument: [],
    statusDocument2: [],
    finalThesis: [],
  });

  const [groupIds, setGroupIds] = useState({
    topicAssessmentForm: '',
    projectCharter: '',
    statusDocument1: '',
    logBook: '',
    proposalDocument: '',
    statusDocument2: '',
    finalThesis: '',
  });

  const topicAssessmentFormInputRef = useRef(null);
  const projectCharterInputRef = useRef(null);
  const statusDocument1InputRef = useRef(null);
  const logBookInputRef = useRef(null);
  const proposalDocumentInputRef = useRef(null);
  const statusDocument2InputRef = useRef(null);
  const finalThesisInputRef = useRef(null);

  const handleDrop = (event, documentType) => {
    event.preventDefault();
    const droppedFiles = Array.from(event.dataTransfer.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...droppedFiles] }));

    // Trigger a click event on the file input
    if (documentType === 'topicAssessmentForm') {
      topicAssessmentFormInputRef.current.click();
    } else if (documentType === 'projectCharter') {
      projectCharterInputRef.current.click();
    } else if (documentType === 'statusDocument1') {
      statusDocument1InputRef.current.click();
    } else if (documentType === 'logBook') {
      logBookInputRef.current.click();
    } else if (documentType === 'proposalDocument') {
      proposalDocumentInputRef.current.click();
    } else if (documentType === 'statusDocument2') {
      statusDocument2InputRef.current.click();
    } else if (documentType === 'finalThesis') {
      finalThesisInputRef.current.click();
    }
  };

  const handleFileInputChange = (event, documentType) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...selectedFiles] }));
  };

  const handleRemoveFile = (documentType, index) => {
    const updatedFiles = [...files[documentType]];
    updatedFiles.splice(index, 1);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: updatedFiles }));
  };

  const handleGroupIdChange = (event, documentType) => {
    setGroupIds((prevGroupIds) => ({ ...prevGroupIds, [documentType]: event.target.value }));
  };

  const handleSubmit = (documentType) => {
    // Add your logic to handle file submission here
    console.log(`Files to be uploaded for ${documentType}:`, files[documentType]);
    console.log(`Group ID for ${documentType}:`, groupIds[documentType]);
    // You can make an API call here to upload the files to the server
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="document-upload-container">
        <h2>Upload Documents</h2>
        {/* Topic Assessment Form Section */}
        <div className="document-section">
          <h3>Topic Assessment Form</h3>
          <div>
            <label htmlFor="topicAssessmentFormGroupId">Group ID:</label>
            <input
              type="text"
              id="topicAssessmentFormGroupId"
              value={groupIds.topicAssessmentForm}
              onChange={(event) => handleGroupIdChange(event, 'topicAssessmentForm')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'topicAssessmentForm')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => topicAssessmentFormInputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="topicAssessmentFormInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'topicAssessmentForm')}
              className="file-input"
              ref={topicAssessmentFormInputRef}
            />
          </div>
          <div className="file-list">
            {files.topicAssessmentForm.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('topicAssessmentForm', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('topicAssessmentForm')}>
            Submit
          </button>
        </div>

        {/* Project Charter Section */}
        <div className="document-section">
          <h3>Project Charter</h3>
          <div>
            <label htmlFor="projectCharterGroupId">Group ID:</label>
            <input
              type="text"
              id="projectCharterGroupId"
              value={groupIds.projectCharter}
              onChange={(event) => handleGroupIdChange(event, 'projectCharter')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'projectCharter')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => projectCharterInputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="projectCharterInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'projectCharter')}
              className="file-input"
              ref={projectCharterInputRef}
            />
          </div>
          <div className="file-list">
            {files.projectCharter.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('projectCharter', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('projectCharter')}>
            Submit
          </button>
        </div>

        {/* Status Document 1 Section */}
        <div className="document-section">
          <h3>Status Document 1</h3>
          <div>
            <label htmlFor="statusDocument1GroupId">Group ID:</label>
            <input
              type="text"
              id="statusDocument1GroupId"
              value={groupIds.statusDocument1}
              onChange={(event) => handleGroupIdChange(event, 'statusDocument1')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'statusDocument1')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => statusDocument1InputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="statusDocument1Input"
              multiple
              onChange={(event) => handleFileInputChange(event, 'statusDocument1')}
              className="file-input"
              ref={statusDocument1InputRef}
            />
          </div>
          <div className="file-list">
            {files.statusDocument1.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('statusDocument1', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('statusDocument1')}>
            Submit
          </button>
        </div>

        {/* Log Book Section */}
        <div className="document-section">
          <h3>Log Book</h3>
          <div>
            <label htmlFor="logBookGroupId">Group ID:</label>
            <input
              type="text"
              id="logBookGroupId"
              value={groupIds.logBook}
              onChange={(event) => handleGroupIdChange(event, 'logBook')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'logBook')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => logBookInputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="logBookInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'logBook')}
              className="file-input"
              ref={logBookInputRef}
            />
          </div>
          <div className="file-list">
            {files.logBook.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('logBook', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('logBook')}>
            Submit
          </button>
        </div>

        {/* Proposal Document Section */}
        <div className="document-section">
          <h3>Proposal Document</h3>
          <div>
            <label htmlFor="proposalDocumentGroupId">Group ID:</label>
            <input
              type="text"
              id="proposalDocumentGroupId"
              value={groupIds.proposalDocument}
              onChange={(event) => handleGroupIdChange(event, 'proposalDocument')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'proposalDocument')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => proposalDocumentInputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="proposalDocumentInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'proposalDocument')}
              className="file-input"
              ref={proposalDocumentInputRef}
            />
          </div>
          <div className="file-list">
            {files.proposalDocument.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('proposalDocument', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('proposalDocument')}>
            Submit
          </button>
        </div>

        {/* Status Document 2 Section */}
        <div className="document-section">
          <h3>Status Document 2</h3>
          <div>
            <label htmlFor="statusDocument2GroupId">Group ID:</label>
            <input
              type="text"
              id="statusDocument2GroupId"
              value={groupIds.statusDocument2}
              onChange={(event) => handleGroupIdChange(event, 'statusDocument2')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'statusDocument2')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => statusDocument2InputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="statusDocument2Input"
              multiple
              onChange={(event) => handleFileInputChange(event, 'statusDocument2')}
              className="file-input"
              ref={statusDocument2InputRef}
            />
          </div>
          <div className="file-list">
            {files.statusDocument2.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('statusDocument2', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('statusDocument2')}>
            Submit
          </button>
        </div>

        {/* Final Thesis Section */}
        <div className="document-section">
          <h3>Final Thesis</h3>
          <div>
            <label htmlFor="finalThesisGroupId">Group ID:</label>
            <input
              type="text"
              id="finalThesisGroupId"
              value={groupIds.finalThesis}
              onChange={(event) => handleGroupIdChange(event, 'finalThesis')}
              placeholder="Enter group ID"
            />
          </div>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'finalThesis')}
            onDragOver={(event) => event.preventDefault()}
            onClick={() => finalThesisInputRef.current.click()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="finalThesisInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'finalThesis')}
              className="file-input"
              ref={finalThesisInputRef}
            />
          </div>
          <div className="file-list">
            {files.finalThesis.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('finalThesis', index)}>Remove</button>
              </div>
            ))}
          </div>
          <button className="upload-button" onClick={() => handleSubmit('finalThesis')}>
            Submit
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Document;
