// RegisterPage.js
import React, { useState } from 'react';
import CoordinatorRegistrationForm from './CoordinatorRegistrationForm';
import MemberRegistrationForm from './MemberRegistrationForm';
import ExaminerRegistrationForm from './ExaminerRegistrationForm';
import SupervisorRegistrationForm from './SupervisorRegistrationForm';
import CoSupervisorRegistrationForm from './CoSupervisorRegistrationForm';

const RegisterPage = () => {
  const [step, setStep] = useState(1);
  const [role, setRole] = useState('');

  const handleRoleSelect = (selectedRole) => {
    setRole(selectedRole);
    setStep(step + 1);
  };

  const handleBack = () => {
    setStep(step - 1);
  };

  return (
    <div>
      {step === 1 && (
        <RoleSelectionForm onSelectRole={handleRoleSelect} />
      )}
      {step === 2 && role === 'coordinator' && (
        <CoordinatorRegistrationForm onBack={handleBack} />
      )}
      {step === 2 && role === 'member' && (
        <MemberRegistrationForm onBack={handleBack} />
      )}
      {step === 2 && role === 'examiner' && (
        <ExaminerRegistrationForm onBack={handleBack} />
      )}
      {step === 2 && role === 'supervisor' && (
        <SupervisorRegistrationForm onBack={handleBack} />
      )}
      {step === 2 && role === 'co-supervisor' && (
        <CoSupervisorRegistrationForm onBack={handleBack} />
      )}
    </div>
  );
};

export default RegisterPage;
