// RoleSelectionPage.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Register.css';
import Logo from '../Components/Images/LOGO1.png';

import StudentRegistrationForm from './StudentRegistrationForm';
import CordinatorRegistrationForm from './CordinatorRegistrationForm';
import ProjectMembersRegistrationForm from './ProjectMembersRegistrationForm';
import ExaminerRegistrationForm from './ExaminerRegistrationForm';
import SupervisorRegistrationForm from './SupervisorRegistrationForm';
import CoSupervisorRegistrationForm from './CoSupervisorRegistrationForm';

const RoleSelectionPage = () => {
  const [selectedRole, setSelectedRole] = useState(null);
  const [showRegistrationForm, setShowRegistrationForm] = useState(false);

  const handleRoleSelect = (role) => {
    setSelectedRole(role);
    setShowRegistrationForm(true);
  };

  const renderRegistrationForm = () => {
    switch (selectedRole) {
      case 'student':
        return <StudentRegistrationForm />;
      case 'cordinator':
        return <CordinatorRegistrationForm />;
      case 'member':
        return <ProjectMembersRegistrationForm />;
      case 'examiner':
        return <ExaminerRegistrationForm />;
      case 'supervisor':
        return <SupervisorRegistrationForm />;
      case 'co-supervisor':
        return <CoSupervisorRegistrationForm />;
      default:
        return null;
    }
  };

  return (
    <div className={`container${showRegistrationForm ? ' form-active' : ''}`}>
      <div className="background-frame">
        <div className="row">
          <div className="col-md-4 role-selection-col">
            <div className="role-nav">
              <h3 className="role-nav-heading">Select Your Role</h3>
              <ul className="role-list">
                <li><button onClick={() => handleRoleSelect('student')}>Student</button></li>
                <li><button onClick={() => handleRoleSelect('cordinator')}>Project Coordinator</button></li>
                <li><button onClick={() => handleRoleSelect('member')}>Project Member</button></li>
                <li><button onClick={() => handleRoleSelect('examiner')}>Examiner</button></li>
                <li><button onClick={() => handleRoleSelect('supervisor')}>Supervisor</button></li>
                <li><button onClick={() => handleRoleSelect('co-supervisor')}>Co-Supervisor</button></li>
              </ul>
            </div>
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <p style={{ color: '#124E66' }}>Already have an account? <Link to="/login" style={{ color: '#124E66', fontWeight: 'bold' }}>Login</Link></p>
            </div>
          </div>
          <div className="col-md-8 registration-form-col">
            <div className="registration-form">
              {renderRegistrationForm()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RoleSelectionPage;
