// SupervisorRegistrationForm.js
import './Register.css';
import React, { useState } from 'react';

const SupervisorRegistrationForm = ({ role }) => {
  const [formData, setFormData] = useState({
    name: '',
    registrationNumber: '',
    contactNumber: '',
    emailAddress: '',
    password: '',
    confirmPassword: '',
    role ,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Placeholder logic for checking uniqueness
    const isRegistrationNumberUnique = true; // Replace with actual logic
    const isEmailUnique = true; // Replace with actual logic

    if (!isRegistrationNumberUnique) {
      console.error('Registration number already exists');
      return;
    }

    if (!isEmailUnique) {
      console.error('Email already exists');
      return;
    }

    // Check if password and confirm password match
    if (formData.password !== formData.confirmPassword) {
      console.error('Password and confirm password do not match');
      return;
    }

    // Handle form submission
    console.log(formData);
  };

  return (
    <div>
      <h2>Supervisor Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-fields">
          <div className="input-field">
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} />
          </div>
          <div className="input-field">
            <label>Registration Number:</label>
            <input type="text" name="registrationNumber" value={formData.registrationNumber} onChange={handleChange} />
          </div>
          <div className="input-field">
            <label>Contact Number:</label>
            <input type="text" name="contactNumber" value={formData.contactNumber} onChange={handleChange} />
          </div>
          <div className="input-field">
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} />
          </div>
          <div className="input-field">
            <label>Password:</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} />
          </div>
          <div className="input-field">
            <label>Confirm Password:</label>
            <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
          </div>
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default SupervisorRegistrationForm;
