// RoleSelectionPage.js
import React, { useState } from 'react';

const RoleSelectionPage = ({ onSelectRole }) => {
  const [selectedRole, setSelectedRole] = useState('');

  const handleRoleChange = (e) => {
    setSelectedRole(e.target.value);
  };

  const handleNext = () => {
    onSelectRole(selectedRole);
  };

  return (
    <div>
      <h2>Select Your Role</h2>
      <div>
        <label>
          <input
            type="radio"
            value="coordinator"
            checked={selectedRole === 'coordinator'}
            onChange={handleRoleChange}
          />
          Coordinator
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            value="member"
            checked={selectedRole === 'member'}
            onChange={handleRoleChange}
          />
          Member
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            value="examiner"
            checked={selectedRole === 'examiner'}
            onChange={handleRoleChange}
          />
          Examiner
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            value="supervisor"
            checked={selectedRole === 'supervisor'}
            onChange={handleRoleChange}
          />
          Supervisor
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            value="co-supervisor"
            checked={selectedRole === 'co-supervisor'}
            onChange={handleRoleChange}
          />
          Co-supervisor
        </label>
      </div>
      <button onClick={handleNext} disabled={!selectedRole}>Next</button>
    </div>
  );
};

export default RoleSelectionPage;
