// Login.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Logo from '../src/Components/Images/LOGO1.png';
import LoginImage from'../src/Components/Images/Login.png'
import './App.css';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    role: 'Student', // Default role selection
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to server)
    console.log(formData);
  };

  return (
    <div className="logo-container">
      <img src={Logo} alt="Logo" className="logo-image" />
      <div className="login-container">
        <div style={{ textAlign: 'center' }}>
          <img src={LoginImage} alt="LoginImage" className="login-image" style={{ width: '100px', height: 'auto' }} />
        </div>

        <h3>Login</h3>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
          <label htmlFor="username">Role</label>
            <select id="role" name="role" value={formData.role} onChange={handleChange}>
              <option value="Student">Student</option>
              <option value="Cordinator">Coordinator</option>
              <option value="Project Member">Project Member</option>
              <option value="Examiner">Examiner</option>
              <option value="Supervisor">Supervisor</option>
              <option value="Co-Supervisor">Co Supervisor</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input type="username" id="username" name="username" value={formData.username} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} />
          </div>

          <button type="submit">Login</button>
        </form>
        <p>Don't have an account? <Link to="/register">Register</Link></p>
      </div>
    </div>
  );
};

export default Login;
