// StudentRegistrationForm.js
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import { Link } from 'react-router-dom';
import Footer from '../Components/Footer';
import React, { useState } from 'react';

const StudentRegistrationForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    registrationNumber: '',
    contactNumber: '',
    email: '',
    batch: '',
    specialization: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div>
      <h2>Student Registration</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Registration Number:</label>
          <input type="text" name="registrationNumber" value={formData.registrationNumber} onChange={handleChange} />
        </div>
        <div>
          <label>Contact Number:</label>
          <input type="text" name="contactNumber" value={formData.contactNumber} onChange={handleChange} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Batch:</label>
          <select name="batch" value={formData.batch} onChange={handleChange}>
            <option value="Regular">Regular</option>
            <option value="June">June</option>
          </select>
        </div>
        <div>
          <label>Specialization:</label>
          <select name="specialization" value={formData.specialization} onChange={handleChange}>
            <option value="IT">Information Technology (IT)</option>
            <option value="SE">Software Engineering (SE)</option>
            <option value="IS">Information Systems (IS)</option>
            <option value="CS">Cyber Security (CS)</option>
            <option value="DS">Data Science (DS)</option>
            <option value="CSNE">Computer Systems and Network Engineering (CSNE)</option>
          </select>
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default StudentRegistrationForm;
