import React, { useState } from 'react';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Proposal.css';

const ProposalMarks = () => {
  const [groupID, setGroupID] = useState('');
  const [marks, setMarks] = useState([
    { studentName: '', registrationNumber: '', marks: '' },
    { studentName: '', registrationNumber: '', marks: '' },
    { studentName: '', registrationNumber: '', marks: '' },
    { studentName: '', registrationNumber: '', marks: '' }
  ]);

  const handleGroupIDChange = (event) => {
    setGroupID(event.target.value);
  };

  const handleInputChange = (event, index) => {
    const newMarks = [...marks];
    newMarks[index] = { ...newMarks[index], [event.target.name]: event.target.value };
    setMarks(newMarks);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission (e.g., send marks to backend)
    console.log('Group ID:', groupID);
    console.log('Marks submitted:', marks);
    // Redirect to enter-marks page after submission
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <h2>Enter Proposal Marks</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Group ID:
          <input type="text" value={groupID} onChange={handleGroupIDChange} />
        </label>
        <br />
        {marks.map((mark, index) => (
          <div key={index}>
            <h3>Member {index + 1}</h3>
            <label>
              Student Name:
              <input type="text" name={`studentName${index}`} value={mark.studentName} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
            <label>
              Registration Number:
              <input type="text" name={`registrationNumber${index}`} value={mark.registrationNumber} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
            <label>
              Marks:
              <input type="text" name={`marks${index}`} value={mark.marks} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
          </div>
        ))}
        <button type="submit">Submit Marks</button>
      </form>
      <Footer />
    </div>
  );
};

export default ProposalMarks;
