import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Proposal.css';
import axios from 'axios';

const Proposal = () => {
  const [groupId, setGroupId] = useState('');
  const [members, setMembers] = useState([
    { name: '', id: '', marks: '', isValid: true },
    { name: '', id: '', marks: '', isValid: true },
    { name: '', id: '', marks: '', isValid: true },
    { name: '', id: '', marks: '', isValid: true }
  ]);
  const [showValidation, setShowValidation] = useState(false);

  const handleGroupIdChange = (event) => {
    setGroupId(event.target.value);
  };

  const handleMemberChange = (event, index, field) => {
    const updatedMembers = [...members];
    updatedMembers[index][field] = event.target.value;
    setMembers(updatedMembers);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setShowValidation(true);

    const incompleteMembers = members.filter(
      (member) => member.name && (!member.id || !member.marks)
    );

    setMembers(
      members.map((member) =>
        incompleteMembers.includes(member) ? { ...member, isValid: false } : { ...member, isValid: true }
      )
    );

    if (groupId && !incompleteMembers.length) {
      try {
        const response = await axios.post('http://localhost:8000/api/students/proposal', {
          groupId,
          members: members.map(({ isValid, ...member }) => member)
        });
        console.log(response.data);
        // Handle success or show a success message
      } catch (error) {
        console.error(error);
        // Handle error or show an error message
      }
    }
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="proposal-container">
        <h2>Enter Proposal Marks</h2>
        <form onSubmit={handleSubmit} className="proposal-form" noValidate>
          <div className="group-id-section">
            <label htmlFor="groupId">Group Registration ID:</label>
            <input
              type="text"
              id="groupId"
              value={groupId}
              onChange={handleGroupIdChange}
              placeholder="Enter group ID"
              required
              className={showValidation && !groupId ? 'invalid' : ''}
            />
            {showValidation && !groupId && (
              <span className="validation-message">Group ID is required.</span>
            )}
          </div>
          <div className="member-sections">
            {members.map((member, index) => (
              <div key={index} className="member-section">
                <h4>Member {index + 1}</h4>
                <div className="member-details">
                  <div>
                    <label htmlFor={`member${index}Name`}>Name:</label>
                    <input
                      type="text"
                      id={`member${index}Name`}
                      value={member.name}
                      onChange={(event) => handleMemberChange(event, index, 'name')}
                      placeholder="Enter member name"
                    />
                  </div>
                  <div>
                    <label htmlFor={`member${index}Id`}>ID:</label>
                    <input
                      type="text"
                      id={`member${index}Id`}
                      value={member.id}
                      onChange={(event) => handleMemberChange(event, index, 'id')}
                      placeholder="Enter member ID"
                      required={member.name}
                      className={showValidation && member.name && !member.id ? 'invalid' : ''}
                    />
                    {showValidation && member.name && !member.id && (
                      <span className="validation-message">Member ID is required.</span>
                    )}
                  </div>
                  <div>
                    <label htmlFor={`member${index}Marks`}>Marks:</label>
                    <input
                      type="number"
                      id={`member${index}Marks`}
                      value={member.marks}
                      onChange={(event) => handleMemberChange(event, index, 'marks')}
                      placeholder="Enter marks"
                      required={member.name}
                      className={showValidation && member.name && !member.marks ? 'invalid' : ''}
                    />
                    {showValidation && member.name && !member.marks && (
                      <span className="validation-message">Marks are required.</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default Proposal;
