import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Document.css';
import GroupIDInput from '../Components/GroupIDInput'; // Import the GroupIDInput component

const Document = () => {
  const [files, setFiles] = useState({
    topicAssessmentForm: [],
    projectCharter: [],
    statusDocument1: [],
    logBook: [],
    proposalDocument: [],
    statusDocument2: [],
    finalThesis: [],
  });

  const [groupIds, setGroupIds] = useState('');

  const topicAssessmentFormInputRef = useRef(null);
  const projectCharterInputRef = useRef(null);
  const statusDocument1InputRef = useRef(null);
  const logBookInputRef = useRef(null);
  const proposalDocumentInputRef = useRef(null);
  const statusDocument2InputRef = useRef(null);
  const finalThesisInputRef = useRef(null);

  const handleDrop = (event, documentType) => {
    event.preventDefault();
    const droppedFiles = Array.from(event.dataTransfer.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...droppedFiles] }));
  };

  const handleFileInputChange = (event, documentType) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...selectedFiles] }));
  };

  const handleRemoveFile = (documentType, index) => {
    const updatedFiles = [...files[documentType]];
    updatedFiles.splice(index, 1);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: updatedFiles }));
  };

  const handleSubmit = () => {
    // Add your logic to handle file submission here
    console.log('Files to be uploaded:', files);
    console.log('Group ID:', groupIds);
    // You can make an API call here to upload the files to the server
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="document-upload-container">
        <h2>Upload Documents</h2>
        <GroupIDInput
          value={groupIds}
          onChange={(event) => setGroupIds(event.target.value)}
        />
        <div className="document-section">
          <h3>Topic Assessment Form</h3>
          {/* Rest of the code... */}
        </div>

        <div className="document-section">
          <h3>Project Charter</h3>
          {/* Rest of the code... */}
        </div>

        {/* Include other document sections similarly */}

        <button className="upload-button" onClick={handleSubmit}>
          Submit
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default Document;
