import React, { useState } from 'react';
import { Redirect } from 'react-router-dom'; // Import Redirect
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';

const ProposalMarks = () => {
  const [marks, setMarks] = useState([]);
  const [redirectToEnterMarks, setRedirectToEnterMarks] = useState(false); // State for redirection

  const handleInputChange = (event, index) => {
    const newMarks = [...marks];
    newMarks[index] = { ...newMarks[index], [event.target.name]: event.target.value };
    setMarks(newMarks);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission (e.g., send marks to backend)
    console.log('Marks submitted:', marks);
    // Set redirection state to true
    setRedirectToEnterMarks(true);
  };

  const addStudent = () => {
    setMarks([...marks, { studentName: '', registrationNumber: '', groupID: '', marks: '' }]);
  };

  if (redirectToEnterMarks) {
    // Redirect to enter-marks page
    return <Redirect to="/enter-marks" />;
  }

  return (
    <div>
      <Header />
      <NavigationBar />
      <h2>Enter Proposal Marks</h2>
      <form onSubmit={handleSubmit}>
        {marks.map((mark, index) => (
          <div key={index}>
            <label>
              Student Name:
              <input type="text" name={`studentName${index}`} value={mark.studentName} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
            <label>
              Registration Number:
              <input type="text" name={`registrationNumber${index}`} value={mark.registrationNumber} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
            <label>
              Group ID:
              <input type="text" name={`groupID${index}`} value={mark.groupID} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
            <label>
              Marks:
              <input type="text" name={`marks${index}`} value={mark.marks} onChange={(e) => handleInputChange(e, index)} />
            </label>
            <br />
          </div>
        ))}
        <button type="button" onClick={addStudent}>Add Student</button>
        <button type="submit">Submit Marks</button>
      </form>
      <Footer />
    </div>
  );
};

export default ProposalMarks;
