import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';


const RoleSelectionPage = () => {
  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="role-selection-container">
        <h2 className="text-center mb-5">Select Your Role</h2>
        <div className="btn-container d-flex flex-column align-items-center">
          <Link to="/register/student" className="btn btn-primary btn-lg mb-3">
            Student
          </Link>
          <Link to="/register/coordinator" className="btn btn-primary btn-lg mb-3">
            Project Coordinator
          </Link>
          <Link to="/register/member" className="btn btn-primary btn-lg mb-3">
            Project Member
          </Link>
          <Link to="/register/examiner" className="btn btn-primary btn-lg mb-3">
            Examiner
          </Link>
          <Link to="/register/supervisor" className="btn btn-primary btn-lg mb-3">
            Supervisor
          </Link>
          <Link to="/register/co-supervisor" className="btn btn-primary btn-lg">
            Co-supervisor
          </Link>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default RoleSelectionPage;
