import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Proposal.css';
import axios from 'axios';

const Proposal = () => {
  const [groupId, setGroupId] = useState('');
  const [proposalMarks, setProposalMarks] = useState('');
  const [showValidation, setShowValidation] = useState(false);

  const handleGroupIdChange = (event) => {
    setGroupId(event.target.value);
  };

  const handleProposalMarksChange = (event) => {
    setProposalMarks(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setShowValidation(true);

    if (groupId && proposalMarks) {
      try {
        const response = await axios.post('http://localhost:8000/api/students/proposal', {
          groupId,
          id: '123456', // Replace '123456' with the actual student ID
          proposalMarks
        });
        console.log(response.data);
        // Handle success or show a success message
      } catch (err) {
        console.error(err);
        // Handle error or show an error message
      }
    }
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="proposal-container">
        <h2>Enter Proposal Marks</h2>
        <form onSubmit={handleSubmit} className="proposal-form" noValidate>
          <div className="group-id-section">
            <label htmlFor="groupId">Group Registration ID:</label>
            <input
              type="text"
              id="groupId"
              value={groupId}
              onChange={handleGroupIdChange}
              placeholder="Enter group ID"
              required
              className={showValidation && !groupId ? 'invalid' : ''}
            />
            {showValidation && !groupId && (
              <span className="validation-message">Group ID is required.</span>
            )}
          </div>
          <div>
            <label htmlFor="proposalMarks">Proposal Marks:</label>
            <input
              type="number"
              id="proposalMarks"
              value={proposalMarks}
              onChange={handleProposalMarksChange}
              placeholder="Enter proposal marks"
              required
              className={showValidation && !proposalMarks ? 'invalid' : ''}
            />
            {showValidation && !proposalMarks && (
              <span className="validation-message">Proposal marks are required.</span>
            )}
          </div>
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default Proposal;
