import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../Components/Header';
import NavigationBar from '../Components/NavigationBar';
import Footer from '../Components/Footer';
import './Document.css';

const Document = () => {
  const [files, setFiles] = useState({
    topicAssessmentForm: [],
    projectCharter: [],
    statusDocument1: [],
    logBook: [],
    proposalDocument: [],
    statusDocument2: [],
    finalThesis: [],
  });

  const handleDrop = (event, documentType) => {
    event.preventDefault();
    const droppedFiles = Array.from(event.dataTransfer.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...droppedFiles] }));
  };

  const handleFileInputChange = (event, documentType) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: [...prevFiles[documentType], ...selectedFiles] }));
  };

  const handleRemoveFile = (documentType, index) => {
    const updatedFiles = [...files[documentType]];
    updatedFiles.splice(index, 1);
    setFiles((prevFiles) => ({ ...prevFiles, [documentType]: updatedFiles }));
  };

  const handleSubmit = () => {
    // Add your logic to handle file submission here
    console.log('Files to be uploaded:', files);
    // You can make an API call here to upload the files to the server
  };

  return (
    <div>
      <Header />
      <NavigationBar />
      <div className="document-upload-container">
        <h2>Upload Documents</h2>
        {/* Topic Assessment Form Section */}
        <div className="document-section">
          <h3>Topic Assessment Form</h3>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'topicAssessmentForm')}
            onDragOver={(event) => event.preventDefault()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="topicAssessmentFormInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'topicAssessmentForm')}
              className="file-input"
            />
          </div>
          <div className="file-list">
            {files.topicAssessmentForm.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('topicAssessmentForm', index)}>Remove</button>
              </div>
            ))}
          </div>
        </div>
        
        {/* Repeat the above pattern for other document types */}
        
        {/* Project Charter Section */}
        <div className="document-section">
          <h3>Project Charter</h3>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'projectCharter')}
            onDragOver={(event) => event.preventDefault()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="projectCharterInput"
              multiple
              onChange={(event) => handleFileInputChange(event, 'projectCharter')}
              className="file-input"
            />
          </div>
          <div className="file-list">
            {files.projectCharter.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('projectCharter', index)}>Remove</button>
              </div>
            ))}
          </div>
        </div>
        
        {/* Status Document 1 Section */}
        <div className="document-section">
          <h3>Status Document 1</h3>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'statusDocument1')}
            onDragOver={(event) => event.preventDefault()}
          >
            <p>Drag & drop files here, or click to select files</p>
            <input
              type="file"
              id="statusDocument1Input"
              multiple
              onChange={(event) => handleFileInputChange(event, 'statusDocument1')}
              className="file-input"
            />
          </div>
          <div className="file-list">
            {files.statusDocument1.map((file, index) => (
              <div key={index} className="file-item">
                <span className="file-name">{file.name}</span>
                <button onClick={() => handleRemoveFile('statusDocument1', index)}>Remove</button>
              </div>
            ))}
          </div>
        </div>
        
        {/* Log Book Section */}
        <div className="document-section">
          <h3>Log Book</h3>
          <div
            className="drop-zone"
            onDrop={(event) => handleDrop(event, 'logBook')}
            onDragOver={(event) => event.preventDefault()}
          >
            <p>Drag & drop files here,// Continuing from the previous code...or click to select files</p>
            <input
  type="file"
  id="logBookInput"
  multiple
  onChange={(event) => handleFileInputChange(event, 'logBook')}
  className="file-input"
/>
</div>
<div className="file-list">
{files.logBook.map((file, index) => (
  <div key={index} className="file-item">
    <span className="file-name">{file.name}</span>
    <button onClick={() => handleRemoveFile('logBook', index)}>Remove</button>
  </div>
))}
</div>
</div>

{/* Proposal Document Section */}
<div className="document-section">
<h3>Proposal Document</h3>
<div
className="drop-zone"
onDrop={(event) => handleDrop(event, 'proposalDocument')}
onDragOver={(event) => event.preventDefault()}
>
<p>Drag & drop files here, or click to select files</p>
<input
  type="file"
  id="proposalDocumentInput"
  multiple
  onChange={(event) => handleFileInputChange(event, 'proposalDocument')}
  className="file-input"
/>
</div>
<div className="file-list">
{files.proposalDocument.map((file, index) => (
  <div key={index} className="file-item">
    <span className="file-name">{file.name}</span>
    <button onClick={() => handleRemoveFile('proposalDocument', index)}>Remove</button>
  </div>
))}
</div>
</div>

{/* Status Document 2 Section */}
<div className="document-section">
<h3>Status Document 2</h3>
<div
className="drop-zone"
onDrop={(event) => handleDrop(event, 'statusDocument2')}
onDragOver={(event) => event.preventDefault()}
>
<p>Drag & drop files here, or click to select files</p>
<input
  type="file"
  id="statusDocument2Input"
  multiple
  onChange={(event) => handleFileInputChange(event, 'statusDocument2')}
  className="file-input"
/>
</div>
<div className="file-list">
{files.statusDocument2.map((file, index) => (
  <div key={index} className="file-item">
    <span className="file-name">{file.name}</span>
    <button onClick={() => handleRemoveFile('statusDocument2', index)}>Remove</button>
  </div>
))}
</div>
</div>

{/* Final Thesis Section */}
<div className="document-section">
<h3>Final Thesis</h3>
<div
className="drop-zone"
onDrop={(event) => handleDrop(event, 'finalThesis')}
onDragOver={(event) => event.preventDefault()}
>
<p>Drag & drop files here, or click to select files</p>
<input
  type="file"
  id="finalThesisInput"
  multiple
  onChange={(event) => handleFileInputChange(event, 'finalThesis')}
  className="file-input"
/>
</div>
<div className="file-list">
{files.finalThesis.map((file, index) => (
  <div key={index} className="file-item">
    <span className="file-name">{file.name}</span>
    <button onClick={() => handleRemoveFile('finalThesis', index)}>Remove</button>
  </div>
))}
</div>
</div>

<button className="upload-button" onClick={handleSubmit}>Upload All Documents</button>
</div>
<Footer />
</div>
);
};

export default Document;
